                Thread.Sleep(1500);
                myCommons.ClassificationCourseCreate(wait, driver, js, i, seleniumEntities);


                myCommons.ClassificationCourseByCode(wait, driver, js, seleniumEntities[i].Code);
                
                
                Thread.Sleep(2000);
                wait.Until(ExpectedConditions.ElementIsVisible(By.Id("dvClassificationCourseCode")));
                myCommons.Log.Information($"{DateTime.Now}: Buscando el Curso de Clasificación creado  " + seleniumEntities[i].Code);

