                Assert.AreEqual(seleniumEntities[i].Code, displayedCode, "El código del Curso Clasificación no coincide.");
                myCommons.Log.Information($"{DateTime.Now}: Validando el Assert por Código del Curso Clasificación  " + seleniumEntities[i].Code);


                string displayedDescription = driver.FindElement(By.Id("dvClassificationCourseDesEs")).Text;
                Assert.AreEqual(seleniumEntities[i].Description, displayedDescription, "La descripción del Curso Clasificación no coincide.");
                myCommons.Log.Information($"{DateTime.Now}: Validando el Assert por Descripción del Curso Clasificación " + seleniumEntities[i].Description);
            }
