using OpenQA.Selenium;

namespace DOLE.HRIS.NUnit.Test.Utilities
{
    public static class Utilities
    {
        public static void SelectOptionByDropDownList(this IJavaScriptExecutor js, IWebElement webElement, string value)
        {
            var script = string.Format("$('#{0} option').each(function (){{if($(this).text() == \"{1}\"){{$(this).attr('selected', true);return false;}}}});", webElement.GetAttribute("id"), value);
            js.ExecuteScript(script);
        }

        public static void CleanFields(this IJavaScriptExecutor js, string controlId)
        {
            string script = string.Format("document.getElementById('{0}').value = '{1}';", controlId, string.Empty);
            js.ExecuteScript(script);
        }
    }
}
